package problem.solving.app;

import problem.solving.list.LinkedList;

import static problem.solving.utilities.StringUtils.join;
import static problem.solving.utilities.StringUtils.split;
import static problem.solving.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
